mapper.select:
SELECT COUNT(REPLY_NUM)
  FROM PRODUCT_REPLY
 WHERE USER_ID = #{userId}
   AND PROD_L_NUM = #{prodLNum}

SELECT PROD_L_NUM ,PROD_L_CODE ,PROD_L_OPTION1 ,PROD_L_OPTION2 ,PROD_L_OPTION3 ,PROD_L_OPTION4 ,PROD_L_OPTION5 ,PROD_L_PRODNM ,PROD_L_PRICE ,PROD_L_UPLOADDATE ,PROD_L_COMPNM ,PROD_L_MAINIMG ,PROD_L_SUBIMG ,PROD_L_DETAIL ,PROD_L_QUANTITY FROM projectdb.PRODUCT_LIST WHERE 1=1
AND PROD_L_OPTION3 = #{prodLOption3}
ORDER BY RAND() LIMIT 4
